import React, { Component } from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';

const app = new Clarifai.App({
  apiKey:'3a72a567db724cd7a41695019867a30a' 
  //'3a72a567db724cd7a41695019867a30a'

});

class App extends Component {
  state ={
    input: '',
    imageUrl: ''
  }

  calculateFaceLocation =(picdata) =>{
    const clarifaiFace = picdata.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');  
    const width=Number(image.width); 
    const height = Number(image.height);
    console.log(width, height); 
  }

  onInputChange = (event) => {
     //console.log(event.target.value);
      this.setState({
        input: event.target.value
      })
  }
//a403429f2ddf4b49b307e318f00e528b
  onSubmit = () => {
     this.setState({imageUrl: this.state.input});
     //https://samples.clarifai.com/face-det.jpg
     //model id: a403429f2ddf4b49b307e318f00e528b
     //"https://samples.clarifai.com/face-det.jpg"
     app.models
     .predict(
         Clarifai.FACE_DETECT_MODEL, 
         this.state.input)
     .then(response => this.RankcalculateFaceLocation(response))
     .catch(err =>console.log(err)); 
        //console.log(response.outputs[0].data.regions[0].region_info.bounding_box);
  }

  render(){
    return (
      <div className="App">
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm  onInputChange={this.onInputChange} onSubmit={this.onSubmit}/>
        <FaceRecognition imageUrl={this.state.imageUrl}/>
      </div>
     );
  }
}
export default App;
