import React, { Component } from 'react';

class Register extends Component{
   render(){

    state={
      registerName: '',
      registerEmail: '',
      registerPassword: ''
    }

    onEmailChange =(event)=>{
        this.setState({registerEmail: event.target.value})
    }
    
    onPasswordChange =(event)=>{
        this.setState({registerPassword: event.target.value})
    }

    onNameChange =(event)=>{
        this.setState({registerName: event.target.value})
    }
    
    onSubmitSignIn = ()=>{
        fetch('http://localhost:3001/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
        .then(response => response.json())
        .then(data =>{
            if(data==='success')
              this.props.onRouteChange('home')
        })
        
    }

    return(
        <article className="br2 ba b--black-10 mv4 w-100 w-50-m w-25-1 mw6 shadow-5 center">
          <main className="pa4 black-80">
            <div className="measure">
              <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                <legend className="f1 fw6 ph0 mh0">Register</legend>
                <div className="mt3">
                  <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
                  <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name"  id="name" />
                </div>
                <div className="mt3">
                  <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                  <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email"  id="email" />
                </div>
                <div className="mv3">
                  <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                  <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password" />
                </div>
              </fieldset>
              <div className="">
                 <input onClick={() => this.props.onRouteChange('home')} 
                  className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                  type="submit" 
                  value="Register" />
              </div>
              
            </div>
          </main>
        </article>
      );
   }
}

export default Register;
